SELECT PROJECT_ID , NVL( CASE WHEN CUSTOM_COMPANY_ID = 'D061' THEN '삼성' WHEN CUSTOM_COMPANY_ID = 'D062' THEN '엘지' WHEN CUSTOM_COMPANY_ID = 'D063' THEN '애플' WHEN CUSTOM_COMPANY_ID = 'D064' THEN '구글' WHEN CUSTOM_COMPANY_ID = 'D065' THEN '아마존' END, '미정') AS CUSTOM_COMPANY_ID , PROJECT_NAME , NVL( CASE WHEN PROJECT_SKILL_LANGUAGE = 'S010' THEN 'JAVA' WHEN PROJECT_SKILL_LANGUAGE = 'S011' THEN 'PYTHON' WHEN PROJECT_SKILL_LANGUAGE = 'S012' THEN 'C++' WHEN PROJECT_SKILL_LANGUAGE = 'S013' THEN 'RUBY' END, '미정') AS PROJECT_SKILL_LANGUAGE, NVL( CASE WHEN PROJECT_SKILL_DB = 'S020' THEN 'ORACLE' WHEN PROJECT_SKILL_DB = 'S021' THEN 'MSSQL' WHEN PROJECT_SKILL_DB = 'S022' THEN 'MYSQL' WHEN PROJECT_SKILL_DB = 'S023' THEN 'POSTGRESQL' END, '미정') AS PROJECT_SKILL_DB , PROJECT_STARTDATE , NVL(PROJECT_ENDDATE, '1900-01-01') AS PROJECT_ENDDATE
FROM ( SELECT PROJECT_ID ,CUSTOM_COMPANY_ID ,PROJECT_NAME ,PROJECT_SKILL_LANGUAGE ,PROJECT_SKILL_DB ,PROJECT_STARTDATE ,PROJECT_ENDDATE ,ROW_NUMBER() OVER(ORDER BY PROJECT_ID DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM PROJECT ) WHERE NUMROW BETWEEN #{startNo} AND #{endNo}
