mapper.select:
/*getTotalCnt*/
SELECT COUNT(*) FROM MEMBER
SELECT COUNT(*) FROM MEMBER WHERE MEMBER_NAME LIKE '%'||#{searchWord}||'%'

mapper.insert:
/*insertMember*/
INSERT 
INTO 
	MEMBER
	(
		MEMBER_ID
  		,MEMBER_NAME
  		,MEMBER_SEX
  		,MEMBER_POSITION
  		,MEMBER_TEL
  		,MEMBER_SKILL_LANGUAGE
  		,MEMBER_SKILL_DB
  		,MEMBER_STARTDATE
  		) 
VALUES 
(
	(
		SELECT 
  			TO_NUMBER(NVL(MAX(MEMBER_ID), 0))+ 1 
  		FROM 
  			MEMBER
  	)
  		,#{member_Name}
  		,#{member_Sex}
  		,#{member_Position}
  		,#{member_Tel}
  		,#{member_Skill_Language}
  		,#{member_Skill_DB}
  		,#{member_startDate}
 )

mapper.update:
UPDATE 
	MEMBER 
SET 
	MEMBER_NAME = #{member_Name}
	,MEMBER_SEX = #{member_Sex}
	,MEMBER_POSITION = #{member_Position}
	,MEMBER_TEL = #{member_Tel} 
	,MEMBER_SKILL_LANGUAGE = #{member_Skill_Language} 
	,MEMBER_SKILL_DB = #{member_Skill_DB} 
	,MEMBER_STARTDATE = #{member_startDate}
	,MEMBER_ENDDATE = #{member_endDate}  
WHERE 
	MEMBER_ID = #{member_Id}
