mapper.select:
SELECT PROJECT_NO ,PROJECT_ID ,CASE WHEN CUSTOM_COMPANY_ID = 'D061' THEN '삼성' WHEN CUSTOM_COMPANY_ID = 'D062' THEN '엘지' WHEN CUSTOM_COMPANY_ID = 'D063' THEN '애플' WHEN CUSTOM_COMPANY_ID = 'D064' THEN '구글' WHEN CUSTOM_COMPANY_ID = 'D065' THEN '아마존' END AS CUSTOM_COMPANY_ID ,PROJECT_NAME ,CASE WHEN PROJECT_SKILL_LANGUAGE = 'S010' THEN 'JAVA' WHEN PROJECT_SKILL_LANGUAGE = 'S011' THEN 'PYTHON' WHEN PROJECT_SKILL_LANGUAGE = 'S012' THEN 'C++' WHEN PROJECT_SKILL_LANGUAGE = 'S013' THEN 'RUBY' END AS PROJECT_SKILL_LANGUAGE ,CASE WHEN PROJECT_SKILL_DB = 'S020' THEN 'ORACLE' WHEN PROJECT_SKILL_DB = 'S021' THEN 'MSSQL' WHEN PROJECT_SKILL_DB = 'S022' THEN 'MYSQL' WHEN PROJECT_SKILL_DB = 'S023' THEN 'POSTGRESQL' END AS PROJECT_SKILL_DB ,PROJECT_STARTDATE ,PROJECT_ENDDATE
FROM ( SELECT PROJECT_NO ,PROJECT_ID ,CUSTOM_COMPANY_ID ,PROJECT_NAME ,PROJECT_SKILL_LANGUAGE ,PROJECT_SKILL_DB ,PROJECT_STARTDATE ,PROJECT_ENDDATE ,ROW_NUMBER() OVER(ORDER BY PROJECT_NO DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM PROJECT
) WHERE NUMROW BETWEEN #{startNo} AND #{endNo}

mapper.insert:
INSERT 
INTO 
	PROJECT
	(
		PROJECT_NO
		,PROJECT_ID
  		,PROJECT_NAME
  		,CUSTOM_COMPANY_ID
  		,PROJECT_SKILL_LANGUAGE
  		,project_Skill_DB
  		,PROJECT_STARTDATE
  	) 
VALUES 
(
	(
		SELECT 
  			TO_NUMBER(NVL(MAX(PROJECT_NO), 0))+ 1 
  		FROM 
  			PROJECT
  	)	
  		,#{project_Id}
  		,#{project_Name}
  		,#{custom_company_id}
  		,#{project_Skill_Language}
  		,#{project_Skill_DB}
  		,#{project_startDate}
 )

update.if:
UPDATE 
	PROJECT 
SET 
	PROJECT_NAME = #{project_Name}
	,CUSTOM_COMPANY_ID = #{custom_company_id}
	,PROJECT_SKILL_LANGUAGE = #{project_Skill_Language} 
	,PROJECT_SKILL_DB = #{project_Skill_DB} 
	,PROJECT_STARTDATE = #{project_startDate} 
	,PROJECT_ENDDATE = #{project_endDate}
WHERE 
	PROJECT_ID = #{project_Id}

UPDATE 
	PROJECT 
SET 
	PROJECT_NAME = #{project_Name}
	,CUSTOM_COMPANY_ID = #{custom_company_id}
	,PROJECT_SKILL_LANGUAGE = #{project_Skill_Language} 
	,PROJECT_SKILL_DB = #{project_Skill_DB} 
	,PROJECT_STARTDATE = #{project_startDate} 
WHERE 
	PROJECT_ID = #{project_Id}

mapper.update:
	UPDATE 
   		PROJECT_DETAIL
   	SET
		PUSHDATE = #{pushDate},
		PULLDATE = #{pullDate}
	WHERE 
		MEMBER_ID = #{member_Id}
	AND
		PROJECT_ID = #{project_Id}
